// @ts-ignore
import BrowserSprite from "svg-baker-runtime/browser-sprite";
import { useLayoutEffect, useEffect } from "react";
var canUseDOM = !!(typeof window !== "undefined" && window.document && window.document.createElement);
var browserSprite;
if (canUseDOM) {
    var spriteId = "__SVG_SPRITE_NODE__";
    browserSprite = new BrowserSprite({
        attrs: {
            id: spriteId
        }
    });
    var mount = function() {
        var spriteNode = document.getElementById(spriteId);
        if (spriteNode) {
            browserSprite.attach(spriteNode);
        } else {
            browserSprite.mount();
        }
        document.removeEventListener("DOMContentLoaded", mount);
    };
    if (document.querySelector("body")) {
        mount();
    } else {
        document.addEventListener("DOMContentLoaded", mount);
    }
} else {
    browserSprite = null;
}
export function addSpriteSymbol(symbol) {
    if (browserSprite) {
        browserSprite.add(symbol);
    }
}
export var useIsomorphicLayoutEffect = canUseDOM ? useLayoutEffect : useEffect;
